$(document).ready(function(){
	var trains = [];
	visualize(trains);
	getTrains();
	setInterval(getTrains, 10000);
});

function visualize(trains){
	var svg = d3.select('svg');
	var circles = svg.selectAll('circle').data(trains);

	var arrivals = circles.enter();

	var maxCount = d3.max(trains);

	//north
	//shady grove 39.128381, -77.169307
	//south
	//franconia springfield 38.764592, -77.171032
	//west
	//reston 38.948469, -77.351260
	//east
	//largo town center 38.901238, -76.841278
	var x = d3.scale.linear()
	  .range([0, 700])
	  .domain([-77.351260, -76.841278]);

	var y = d3.scale.linear()
	  .range([700, 0])
	  .domain([38.764592, 39.128381]);

	arrivals.append('circle')
	.style("fill", "white")
	.transition()
  .duration(500)
	.attr('cx', function(d, i) {
	  return x(findStation(d.LocationCode).Lon);
	})
	.attr('cy', function(d, i) {
	  return y(findStation(d.LocationCode).Lat);
	})
	.attr('r', 10)
	.style("fill", function(d, i){
		return "#" + colorize(d.Line);
	});
}

